import "./provableAPI.sol";
import './usingModifiers.sol';


pragma solidity 0.5.12;

contract Betting is usingModifiers, usingProvable {

    uint constant MAX_INT_FROM_BYTE = 256;
    uint constant NUM_RANDOM_BYTES_REQUESTED = 1;

    event LogNewProvableQuery(string description);
    event BetPlaced(bytes32 queryId, address player, uint amount);
    event BetFailed(bytes32 queryId, string reason);
    event BetLost(bytes32 queryId, address player, uint amount);
    event BetWon(bytes32 queryId, address player, uint amount);
    event BetFinished(bytes32 queryId);

    struct Bet {
        bytes32 id;
        address payable player;
        uint amount;
        bool betWon;
        bool isPending;
    }

    Bet[] public bets;

    mapping (address => Bet) public playerToBet;
    mapping (address => uint) public playerBetCount;

    constructor() public {
        provable_setProof(proofType_Ledger);
        update();
    }

    function bet(uint _amount) public payable
        costs(0.01 ether)
        setBettingLimit()
        mustHaveRequiredFunds(msg.sender, _amount)
        mustHaveRequiredFunds(address(this), _amount)
    {
        Bet storage thisBet = playerToBet[msg.sender];
        thisBet.player = msg.sender;
        thisBet.amount = _amount;
        thisBet.isPending = true;
        playerBetCount[msg.sender] += 1;
        bytes32 queryId = update();
        thisBet.id = queryId;
        bets.push(thisBet);
        emit BetPlaced(queryId, msg.sender, _amount);
    }

    function __callback(bytes32 _queryId, string memory _result, bytes memory _proof) public {
        require(msg.sender == provable_cbAddress());

        if (provable_randomDS_proofVerify__returnCode(_queryId, _result, _proof) != 0) {
            emit BetFailed(_queryId, "The bet was unable to be verified");
            return;
        } else {
            uint ceiling = (MAX_INT_FROM_BYTE ** NUM_RANDOM_BYTES_REQUESTED) - 1;
            uint randomNumber = uint(keccak256(abi.encodePacked(_result))) % ceiling;

            // for (uint index = 0; index <= bets.length; index++) {
            //     if (bets[index].id == _queryId && bets[index].isPending == true) {
            //         bets[index].isPending == false;

            //         // Player lost
            //         if (randomNumber <= 50) {
            //             bets[index].betWon = false;
            //             emit BetLost(_queryId, bets[index].player, bets[index].amount);

            //         // Player Won
            //         } else if (randomNumber >= 51) {
            //             bets[index].betWon = true;
            //             address(bets[index].player).transfer(bets[index].amount * 2);
            //             emit BetWon(_queryId, bets[index].player, bets[index].amount);
            //         }
            //     }
            // }
            emit BetFinished(_queryId);
        }

    }

    function update() payable public returns (bytes32) {
        uint QUERY_EXECUTION_DELAY = 0;
        uint GAS_FOR_CALLBACK = 200000;
        bytes32 queryId = provable_newRandomDSQuery(QUERY_EXECUTION_DELAY, NUM_RANDOM_BYTES_REQUESTED, GAS_FOR_CALLBACK);
        emit LogNewProvableQuery("Provable query was sent, standing by for the answer...");
        return queryId;
    }
}
