import './PseudoRandomness.sol';
import './Ownable.sol';


pragma solidity 0.5.12;

contract CoinFlip is PseudoRandomness, Ownable {
    // TODO:
    // 1: User can send money and bet on 50/50 flip
    // 2: FrontEnd connection
    // 3: User should be able to place a bet with an amount given
    // 4: User should get back double the bet if they win
    address payable public owner = msg.sender;
    uint public contractBalance;

    event BetPlaced(address gambler, uint amount, bool betWon);
    event ContractFunded(address funder, uint amount);

    modifier costs(uint cost) {
        require(msg.value >= cost);
        _;
    }

    function bet(address payable _gambler, uint _amount) public payable costs(0.01 ether) returns (bool) {
        require(_amount == msg.value, "You must send the amount specified");
        require(_gambler == msg.sender, "You can't use someone else's address");
        require(_gambler.balance >= msg.value, "You have insufficient funds");
        require(contractBalance == _amount, "This contract has insufficient funds");

        contractBalance += _amount;
        bool _betWon;
        uint rand = random();

        if (rand == 1) {
            contractBalance -= _amount * 2;
            _gambler.transfer(_amount * 2);
            _betWon = true;
        } else {
            _betWon = false;
        }

        emit BetPlaced(_gambler, _amount, _betWon);
        return _betWon;
    }

    function fundContract(uint _amount) public payable costs(0.01 ether) returns (uint) {
        require(msg.sender.balance >= _amount, "You have insufficient funds");
        require(msg.value == _amount, "You must send the amount specified");
        contractBalance += _amount;

        emit ContractFunded(msg.sender, _amount);
        return contractBalance;
    }

    function withdrawAll() public onlyOwner returns (uint) {
       uint withdrawalAmount = contractBalance;
       contractBalance = 0;
       owner.transfer(withdrawalAmount);

       return withdrawalAmount;
   }

    function getContractBalance() public view returns (uint) {
        return contractBalance;
    }

    function getOwnerBalance() public view returns (uint) {
        return address(owner).balance;
    }
}
